css:
.print-format {
    font-size: 13px;
    background: white;
}

.print-heading {
    border-bottom: 1px solid var(--dark-border-color);
    padding-bottom: 10px;
    margin-bottom: 20px;
}

.print-heading h2 {
    font-size: 24px;
}

.print-heading h2 div {
    margin-bottom: var(--margin-sm);
    text-transform: uppercase;
}

.print-heading small {
    font-size: 14px !important;
    font-weight: normal;
    color: var(--gray-600);
}

.print-format .letter-head {
    margin-bottom: 30px;
}

.print-format label {
    font-weight: normal;
    font-size: 13px;
    color: var(--text-light);
}

.print-format .data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.print-format .value {
    color: var(--text-color);
}

.print-format .section-break {
    margin-bottom: 15px;
}

.print-format .row {
    line-height: 2;
    margin-top: var(--margin-xs) !important;
}

.print-format .important .value {
    font-size: 13px;
    font-weight: bold;
}

.print-format th {
    color: #74808b;
    font-weight: normal;
    border-bottom-width: 1px !important;
}

.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid var(--dark-border-color);
}

.print-format .table-bordered {
    border: 1px solid var(--dark-border-color);
}

.print-format td, .print-format th {
    padding: var(--padding-sm) !important;
}

.print-format .primary.compact-item {
    font-weight: normal;
}
