css:
.print-format {
    font-size: 13px;
    background: white;
}

.print-heading {
    border-bottom: 1px solid #E2E6E9;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

.print-heading h2 {
    font-size: 24px;
}

.print-heading h2 div {
    margin-bottom: 10px;
    text-transform: uppercase;
}

.print-heading small {
    font-size: 14px !important;
    font-weight: normal;
    color: #74808B;
}

.print-format .letter-head {
    margin-bottom: 30px;
}

.print-format label {
    font-weight: normal;
    font-size: 13px;
    color: #4C5A67;
}

.print-format .data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.print-format .value {
    color: #192734;
}

.print-format .section-break:not(:last-child) {
    margin-bottom: 15px;
}

.print-format .row {
    line-height: 2;
    margin-top: 5px !important;
}

.print-format .important .value {
    font-size: 13px;
    font-weight: bold;
}

.print-format th {
    color: #74808b;
    font-weight: normal;
    border-bottom-width: 1px !important;
}

.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid #E2E6E9;
}

.print-format .table-bordered {
    border: 1px solid #E2E6E9;
}

.print-format td, .print-format th {
    padding: 10px !important;
}

.print-format .primary.compact-item {
    font-weight: normal;
}
